+global.Worker = class {
+  constructor(workerScript) {
+    this.onmessage = (callback) => {
+      try {
+        const result = eval(workerScript);
+        callback({ data: { result } });
+      } catch (error) {
+        callback({ data: { error: error.message } });
+      }
+    };
+    this.postMessage = jest.fn();
+    this.terminate = jest.fn();
+  }
